sections.code:
/* Styles the payment form iframe */
  body, label, .popup {
    background: transparent !important;
    color: #bebebe !important;
  }
  input, select {
    background: #181818 !important;
    color: #bebebe !important;
    border-color: #343434 !important;
  }
  select {
    background: #181818 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAUCAQAAAAua3X8AAAARklEQVR4AWNABWcazjQg8zGl/wNhA15piBL80gglmNKYSjClMZUgpDGVwKXxKBl4QNiRBLxJIKAIBzXhyCIc3YQTDMEkBwBplPJRxEMa4wAAAABJRU5ErkJggg==") no-repeat right 8px center !important;
    background-size: 8px 10px !important;
  }
  input:focus, select:focus {
    border-color: rgb(79, 140, 201) !important;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, .075), 0 0 2px rgb(79, 140, 201) !important;
    outline-color: rgb(79, 140, 201) !important;
  }
  .btn-submit {
    background: linear-gradient(#407045, #305530) !important;
    border-color: #083 !important;
  }
  .btn-submit:hover {
    background: linear-gradient(#508055, #407045) !important;
  }

/* Styles the actions visual editor */
  .editor-node.bg-white, .editor-node.bg-gray-light {
    background: #1c1c1c !important;
  }
  .ActionsTimeline-item.selected {
    background: #222 !important;
  }
  .blankslate-box, .editor-node {
    background: #242424 !important;
  }
  .overlay {
    background: rgba(27, 31, 35, .5) !important;
  }
  svg + div .circle, .port .circle, .circle.live:after {
    background: rgb(79, 140, 201) !important;
  }
  .editor-node p code {
    background: none !important;
  }
  /* the order of these matters */
  .input {
    background-image: linear-gradient(#484848 0%, #484848 50%, #202020 50%, #202020 100%) !important;
  }
  .input.small {
    background-image: linear-gradient(#484848 0%, #484848 50%, #1c1c1c 50%, #1c1c1c 100%) !important;
  }
  .input.isEditing {
    background-image: linear-gradient(rgb(79, 140, 201) 0%, rgb(79, 140, 201) 50%, #202020 50%, #202020 100%) !important;
  }
  .input.hasErrors {
    background-image: linear-gradient(#f44 0%, #f44 50%, #1c1c1c 50%, #1c1c1c 100%) !important;
  }
  .bg-white .input:not(.circle):before, .port:before {
    background-color: #1c1c1c !important;
  }
  .input:not(.small):before, .port.isEditing:before {
    background-color: #202020 !important;
  }
  .event:before {
    background-color: #242424 !important;
  }
  .output {
    background-image: linear-gradient(#1c1c1c 0%, #1c1c1c 50%, #484848 50%, #484848 100%) !important;
  }
  .output.small {
    background-image: linear-gradient(#1c1c1c 0%, #1c1c1c 50%, #484848 50%, #484848 100%) !important;
  }
  .output.event {
    background-image: linear-gradient(#242424 0%, #242424 50%, #484848 50%, #484848 100%) !important;
  }
  .border-blue .output.event.live {
    background-image: linear-gradient(#242424 0%, #242424 50%, rgb(79, 140, 201) 50%, rgb(79, 140, 201) 100%) !important;
  }
  .output.isEditing {
    background-image: linear-gradient(#202020 0%, #202020 50%, rgb(79, 140, 201) 50%, rgb(79, 140, 201) 100%) !important;
  }
  .output.hasErrors {
    background-image: linear-gradient(#1c1c1c 0%, #1c1c1c 50%, #f44 50%, #f44 100%) !important;
  }
  .ActionsTimeline-item {
    border-left-color: #444 !important;
  }
  .ActionsTimeline-item.selected {
    border-color: #333 #444 !important;
  }
  .blankslate-box, .editor-node, .editor-node > div {
    border-color: #484848 !important;
  }
  .blankslate-box.is-hovering,
  .editor-node[style*="box-shadow: rgba(3, 102, 214, 0.3)"] {
    border-color: rgb(79, 140, 201) !important;
    box-shadow: 0 0 0 .2em rgba(79, 140, 201, .3) !important;
    box-shadow: 0 0 0 .2em rgba(79, 140, 201, .3) !important;
  }
  .ActionsTimeline-item--failure.selected {
    border-left-color: #f44 !important;
  }
  .ActionsTimeline-item--pending.selected {
    border-left-color: #cb4 !important;
  }
  .editor-node.border-red {
    border-color: #f44 !important;
    box-shadow: 0 0 0 .2em rgba(255, 68, 68, .3) !important;
  }
  .ActionsTimeline-item--success.selected {
    border-left-color: #6cc644 !important;
  }
  .arrow-icon-container {
    color: rgb(79, 140, 201) !important;
  }
  /* Line between actions */
  [stroke="#959da5"] {
    stroke: #484848 !important;
  }
  [stroke="#dbab0a"] {
    stroke: #cb4 !important;
  }
  /* Active line between actions */
  [stroke="#2188ff"] {
    stroke: rgb(79, 140, 201) !important;
  }
  /* These class names are dynamic and will change frequently 😢 */
  /* editor & read-only bg */
  .jsx-1812009678 {
    background-color: #181818 !important;
  }
  /* run history bg */
  .jsx-2157058810 {
    background-color: transparent !important;
  }
  /* The dot when creating a new action */
  svg + .jsx-1812009678 {
    background: none !important;
  }
  /* create new workflow link */
  .css-15cskdj {
    color: rgb(79, 140, 201);
  }
  /* workflow title, conflict with main page style */
  p code {
    background: none !important;
  }
  /* sidebar */
  /* https://github.com/xt0rted/GitHub-Dark/blob/github-actions/.github/main.workflow */
  .height-full[style="min-width:320px;max-width:320px;overflow-y:auto"] {
    background: #181818;
  }

h1 a, h2 a, h3 a, h4 a, h5 a, h6 a, h4 a > strong code {
    color: #bebebe;
  }
  h3 a {
    color: rgb(79, 140, 201) !important;
  }
  .directory-toc h3 a {
    color: #bebebe !important;
  }
  .nav-mobile-burgerIcon {
    filter: invert(100%) hue-rotate(180deg) !important;
  }
  /* Using #fff over currentColor because GH relies on browser default #000 */
  svg:not([fill]) {
    fill: #fff;
  }
  .hljs {
    color: var(--ghd-code-color, #ccc);
  }

