  }
  #topic-progress-wrapper {
    .timeline-fullscreen, #topic-progress, .bg { c: cm t t }
    .topic-timeline { .username, .title a { c: ac }}
  }

  // New topic modal/comment modal.
  .open .grippie { c: 0 0 ac }
  #reply-control {
    c: fg bd bg;
    box-shadow: none !important;
    .reply-to .action-title, #draft-status, #file-uploading { c: fg }
    .save-or-cancel .cancel { c: red }
  }
  .select-kit.dropdown-select-box {
    .select-kit-body { c: fg bd bg }
    &.toolbar-popup-menu-options .select-kit-row {
      c: cm 0 bg;
      &:hover .name { c: ac }
      .name, .d-icon { c: fg }
    }
    .select-kit-row .texts .name { c: ac }
    .select-kit-row .texts .desc { c: fg }
  }
  .composer-popup { c: cm bd bg }

  // User pages.
  .user-content { c: 0 0 bg }
  .user-main .about {
    .details, &.collapsed-info .details { c: 0 bd bg }
    &.collapsed-info .controls ul a, & .controls a { c: fg 0 bd }
    .secondary {
      c: 0 bd bg;
      dt { c: fg }
      dd { c: cm }
    }
  }
  .stats-section .value { c: fg }
  .user-main .d-icon-heart { c: red }
  // Top borders.
  .top-sub-section li { c: 0 bd }
  // Other usernames.
  .user-info .user-detail .username a { c: cm }
  .user-info .user-detail .name { c: fg }
  // Text colors.
  .links-section .domain { c: fg }
  .top-sub-section .topic-info { c: fg }
  // User activity.
  .user-stream {
    .item, .user-stream-item { c: 0 sh bd }
    .time, .delete-info { c: fg }
  }
  .user-navigation .nav-stacked a, .controls > .btn[href] { c: cm }
  // Logged user settings.
  .user-preferences {
    .static { c: cm }
    .instructions { c: fg }
  }

  // About.
  .user-preferences .bio-composer, .group-form-bio, .edit-category-tab-topic-template {
    c: 0 sh;
    & .d-editor-preview { c: 0 0 bg }
    & textarea { c: fg }
  }
  .d-editor {
    &-spacer, &-button-bar { c: 0 0 sh }
    &-textarea-wrapper {
      c: fg bd bd;
      textarea { c: fg bd bg }
    }
    // Insert link.
    & .d-editor-modal { c: fg bd bg }
  }

  // Emojis.
  .emoji-picker {
    c: fg bd bg;
    box-shadow: bsh;
    .categories-column { c: 0 bd bg }
    .filter {
      c: 0 bd;
      .clear-filter .fa, .d-icon-search .fa {
        c: cm;
        &:hover { c: fg }
      }
    }
    .section-header .title { c: cm }
    button.emoji:hover { c: 0 0 bg }
    .footer { c: 0 bd }
  }

  // Upload image.
  .uploaded-image-preview { color: 0 0 bd }
  .upload-selector .radios .inputs input[type="file"] { c: fg }

  // Modals.
  .modal {
    &-header { c: fg bd }
    &-inner-container {
      c: fg bd bg;
      box-shadow: bsh !important;
    }
    .modal-body label { c: cm }
    // History.
    &.history-modal {
      #revision-details, tbody { c: 0 sh }
      .diff-ins { c: cm 0 green }
    }
  }

  // Notifications.
  .user-menu .notifications li {
    c: fg bd bg;
    &:hover, &:focus { c: 0 0 bd }
    span { c: ac }
  }
  div.menu-links-header a {
    color: cm;
    &:hover, &:focus { c: ac 0 bg }
  }

  // Login/register.
  #simple-container { c: 0 0 bd }
  #login-buttons:not(.hidden) { c: 0 bd }
  button#login-link, button#new-account-link { c: cm }
  #login-form a { c: fg }
}

@-moz-document regexp("https?://docs.gitlab.com(/.*)") {
  // * Landing page.
  .landing {
    // Search container.
    .landing-header {
      c: fg 0 bg;
      box-shadow: 0 1px 0 0 bd, 0 2px 8px 0 sh;
      // Navbar.
      &-top {
        c: 0 bd bg;
        box-shadow: 0 1px 0 0 bd, bsh;
        // Items.
        .header-link {
          transition: ts;
          text-decoration: none !important;
          &:hover p { c: ac }
        }
      }
      // Search.
      .input-container {
        img { filter: invert(var(--bg-invert)) }
        input {
          border: 1px solid bd;
          rad: r;
          c: fg 0 hl;
          &:focus { box-shadow: 0 4px 16px -4px sh; }
        }
      }
    }
    // Cards.
    .main-topics li {
      rad: r;
      border: 1px solid;
      c: 0 bd bg;
      box-shadow: 0 4px 16px -4px sh !important;
    }
    // Most popular topics.
    .topic h4 { c: 0 bd }
  }

  // * Navigation.
  .header {
    c: 0 bd bg;
    // Search bar.
    #search-form {
      c: cm;
      // Found on docs/.../archives page.
      .docsearch {
        rad: r;
        c: cm 0 bd;
      }
      input[type="text"] {
        padding: 2px 11px 2px 28px;
        line-height: 30px;
        rad: r;
        c: fg bd;
      }
    }
    // Docs versions.
    .btn-versions { c: cm bd t }
    .versions-tooltip { c: fg 0 bg }
    // Main navigation.
    .nav-item a { c: fg }
    .nav-item:hover a { c: ac }
  }

  // Cards
  .main-topics li .wrapper h2 {
    border-color: bd !important;
  }

  // Footer
  .footer {
    c: 0 0 bg;
    box-shadow: 0 -1px 0 0 bd, 0 -2px 4px 0 sh;
    // Colorize icons.
    a:hover .fa { c: ac }
  }

  // Logo
  if ( dark_logo == 1 ) {
    .landing .landing-header-top .logo, .header img {
      height: 0 !important;
      width: 0 !important;
      padding: 40px 0 0 40px !important;
      background-image: url("https://gitlab.com/vednoc/dark-gitlab/raw/master/images/docs_logo.png") !important;
      background-size: 40px 40px;
      background-repeat: no-repeat;
    }

    .footer-logo path.logo-dark-orange-shape:nth-child(4), .footer-logo path.logo-light-orange-shape:nth-child(3) {
      dark-logo: t, 0, 0, 0, 0;
    }
    .footer-logo path.logo-svg-shape:nth-child(1) {
      dark-logo: 0, t, 0, 0, 0;
    }
    .footer-logo path.logo-dark-orange-shape:nth-child(7), .footer-logo path.logo-light-orange-shape:nth-child(6) {
      dark-logo: 0, 0, t, 0, 0;
    }
    .footer-logo path.logo-orange-shape:nth-child(2) {
      dark-logo: 0, 0, 0, t, 0;
    }
    .footer-logo path.logo-orange-shape:nth-child(5) {
      dark-logo: 0, 0, 0, 0, t;
    }
  }

  // * Main content.
  .main {
    c: 0 bd;
    h1 { c: 0 bd }
    img { box-shadow: none !important; }
    * > a { c: ac }
    // Help and feedback section.
    .comments {
      padding: 16px;
      margin: 16px auto -16px auto;
      rad: r;
      border: 1px solid;
      c: 0 bd bg;
      .nav-tabs {
        margin: 0;
        c: 0 bd;
        li a {
          border-bottom: none;
          c: cm t t;
          &:first-child { rad: r 0 0 r }
          &.active { c: fg bd bd }
        }
      }
      // Buttons.
      .comments-filter a.comments-holder {
        c: 0 bd bg;
        box-shadow: none !important;
      }
      .tab-content .row {
        margin-top: 16px;
        c: 0 bd;
      }
    }
  }
  .anchor {
    filter: invert(1);
  }
  .badge-small:hover::after {
    c: ac sh bd;
    box-shadow: bsh !important;
  }

  // * Left sidebar.
  .nav-wrapper {
    c: 0 bd bg;
    .global-nav {
      c: 0 0 t;
      &-cat.active a { c: ac }
      .collapse-toggle {
        c: 0 0 t;
        // transition: ts;
        &::after { c: 0 cm }
        &.active { c: 0 0 bd }
      }
      &-section.expanded {
        c: 0 0 t;
        box-shadow: inset 4px 0 0 ac;
      }
      a.active { c: cm ac bd }
      &-block-top a {
        color: fg;
        text-shadow: none;
      }
      &-link {
        color: cm;
        box-shadow: none;
        transition: 0s;
        &:hover, &:focus { &, + .collapse-toggle { c: fg 0 hl }}
        &.active[href] { c: ac }
      }
      .nav-link:hover { c: 0 0 hl }
    }
    // Collapse sidebar.
    .nav-toggle {
      c: 0 t t;
      box-shadow: 0 -1px bd !important;
      &:hover { c: 0 0 hl }
      .label { c: fg }
      .arrow { &::before, &::after { c: 0 0 cm }}
    }
  }

  // * Right sidebar.
  .doc-nav {
    right: -12px;
    height: calc(100vh + 20px);
    c: cm 0 t;
    // Wrapper.
    #markdown-toc {
      a {
        text-decoration: none;
        c: cm;
        &:hover { c: fg }
        &.active { c: ac }
      }
    }
  }

  // * Search results page.
  // ? https://docs.gitlab.com/search/?q=test&idx=gitlab&p=1
  .instantsearch-input {
    .ais-search-box {
      c: 0 0 t;
      &--input {
        rad: r;
        c: fg bd bg;
        box-shadow: bsh;
        &:focus { c: 0 ac }
      }
      &--magnifier { pointer-events: none }
    }
    .search-results {
      .ais-stats {
        &, .stats {
          opacity: 1;
          c: cm;
        }
      }
      .ais-header {
        c: 0 bd;
        + .ais-body {
          label { c: fg }
          label > span { c: fg 0 bd }
        }
      }
      // Results.
      .hit {
        &-content {
          padding: 8px;
          font-size: 1rem;
          c: 0 t;
          box-shadow: none;
          em { c: ac 0 t }
        }
        &-text { c: cm }
        &-tag {
          rad: 0 0 3px 3px;
          c: fg t t;
        }
      }
      .ais-infinite-hits {
        rad: r;
        c: 0 0 bg;
        box-shadow: inner;
        &__empty { padding: 8px }
        &--item:not(:first-child) { box-shadow: 0 -1px bd }
        &--showmore > button {
          rad: r;
          border: 1px solid;
          c: cm bd bg;
        }
      }
    }
  }

  // * Code blocks.
  pre.highlight {
    rad: r;
    c: 0 bd hl;
    box-shadow: bsh;
    code {
      white-space: pre-wrap;
      word-break: break-all;
      rad: inherit;
      c: 0 t;
    }
    // Copy to clipboard.
    .clip-btn {
      top: 1px; right: 1px;
      rad: 0 r 0 0;
      c: cm 0 hl;
      .fa-clipboard::before { color: cm; }
    }
    // Syntax highlighting.
    code, .na, .c, .c1 { c: cm }
    .n, .p, .nb { c: fg }
    .pi { c: red }
    .na { c: cm }
    .s1, .s2 { c: green }
    .no, .kp, .k { c: violet }
    .nt { c: blue }
    .se, .err { c: orange }
    .o, .s, .ss { c: cyan }
  }

  // Footer -> Sources.
  .source-link {
    c: cm 0 bg;
    box-shadow: 0 -1px bd;
    a[href] { c: ac }
  }
}

@-moz-document domain("status.gitlab.com") {
  // * Header.
  #statusio_header {
    margin: 0;
    display: flex;
    align-items: center;
    justify-content: space-between;
    // ? Make the header image smaller.
    #statuspage_header_image {
      float: left;
      width: 100%;
      max-width: 400px;
      &::before, &::after { clear: both; }
    }
  }

  // * Status bar.
  [style*="background:#C44031"] { c: 0 0 red }
  [style*="background:#27AE60"] { c: 0 0 green }
  [style*="background:#00AAF0"] { c: 0 0 blue }
  [style*="background:#FFA837"] { c: 0 0 yellow }

  // * About section.
  .section_border_1 {
    c: 0 bd bg;
    a { c: ac }
    // Icons and text elements nested within them.
    .fa {
      span { c: fg }
      &-check { c: green }
    }
  }

  // * Table.
  .status_td { c: cm bd bg }

  // * History timeline.
  #statusio_history .history-filter-text, .back-text { c: cm }
  #statusio_history_timeline .timelineContainer {
    c: 0 bd;
    .incident_time { c: fg }
    .timelineMajor h2 {
      background-image: none !important;
      &::after {
        content: "";
        position: absolute;
        margin-top: 20px;
        left: 16px;
        width: 24px;
        height: 2px;
        c: 0 0 bd;
      }
    }
    .timelineMinor dt {
      background: none;
      .open, .closed { c: cm }
      &::before {
        content: "";
        position: absolute;
        margin-top: 20px;
        left: 0px;
        width: 12px;
        height: 2px;
        c: 0 0 bd;
      }
    }
    hr { c: 0 0 bd }
  }

  // * Labels.
  .label-success { c: fg 0 green }
  .label-info { c: fg 0 blue }

  // * Rounded corners.
  .make_round {
    rad: r;
    &.make_round_bottom_only { rad: 0 0 r r; }
    &.panel-heading { rad: r r 0 0; }
  }
  .component {
    &_first { rad: r r 0 0; }
    &_last { rad: 0 0 r r; }
  }

  // * Footer.
  #bottom_menu {
    a { c: ac }
    strong a { text-decoration: underline; }
  }
}

@-moz-document domain("next.gitlab.com") {
  body {
    background: none !important;
    .wrapper > img.bg { opacity: 0 }
  }
  .white-box {
    rad: r;
    border: 1px solid;
    c: fg bd bg;
    a { c: ac }
    .text-switch { c: cm }
    .switch .cmn-toggle-round {
      + label { c: 0 0 bd }
      + label::after { c: 0 0 bd }
      + label::before { c: 0 0 bg }
      &:checked + label::before { c: 0 0 blue }
    }
  }
  .footer a { c: fg }
}

sections.code:
:root {
    --note: "Dark-GitLab v0.52.0";
    --bshadow: 0 2px 4px var(--shadow);
    --t: transparent !important;
    --white: #fff;
    --width: 100%;
    --radius: 4px;
    --avatar: 8px;
    --ts: 0.15s ease;
    --ui-font: 'font_name', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Noto Sans', Ubuntu, Cantarell, 'Helvetica Neue', var(--emoji-font), sans-serif;
    --mono-font: 'font_name', 'Menlo', 'DejaVu Sans Mono', 'Liberation Mono', 'Consolas', 'Ubuntu Mono', 'Courier New', 'andale mono', 'lucida console', monospace;
    --emoji-font: 'font_name', 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
    --bg-invert: 0;
    --bg-filter: opacity(1) invert(var(--bg-invert));
    --bg-size: cover;
    --bg-repeat: repeat;
    --bg-pos: center;
    --background: rgba(0,0,0,0.145) url("full-img-URL");
    --dark: #292929;
    --darken: #333;
    --darker: #424242;
    --light: #e6e6e6;
    --lighter: #bababa;
    --accent: #bf692b;
    --shadow: rgba(0,0,0,0.145);
    --yellow: #f0c674;
    --orange: #d98245;
    --red: #a54242;
    --magenta: #b294bb;
    --violet: #85678f;
    --blue: #54738c;
    --cyan: #5e8d87;
    --green: #7b823a;
  }
  html {
    scrollbar-color: var(--darker) var(--dark);
  }
  ::-webkit-scrollbar {
    width: 12px;
  }
  ::-webkit-scrollbar-track {
    background: var(--dark);
  }
  ::-webkit-scrollbar-thumb {
    background: var(--darker);
  }
  ::placeholder {
    color: var(--lighter) !important;
  }
  ::-webkit-input-placeholder {
    color: var(--lighter) !important;
  }
  body {
    color: var(--light) !important;
    background-color: var(--t) !important;
    font-family: var(--ui-font);
  }
  body.modal-open {
    padding-right: 0px !important;
  }
  body.navless {
    color: var(--light) !important;
    background-color: var(--t) !important;
    height: calc(100% - 41px);
  }
  body.navless .navbar-empty {
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
  }
  body header.site-header {
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
  }
  body .footer-fixed {
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
  }
  body .footer-fixed + .footer-container {
    max-width: 100%;
    background-color: var(--dark) !important;
  }
  body time {
    color: inherit;
  }
  body h1,
  body h2,
  body h3,
  body h4,
  body h5,
  body h6,
  body p {
    color: var(--light) !important;
  }
  body ul,
  body ol {
    color: var(--light);
  }
  body a {
    transition: var(--ts);
    color: var(--accent);
  }
  body a:hover,
  body a:focus {
    color: var(--accent);
  }
  body hr {
    margin: 16px auto;
    border-color: var(--darker) !important;
    background-color: var(--t) !important;
  }
  body img.transparent,
  body img.lazy {
    border-color: var(--darker) !important;
    background-color: var(--t) !important;
  }
  body kbd {
    padding: 0.2rem 0.4rem;
    color: var(--light);
    background-color: var(--darken);
    box-shadow: inset 0 0 0 1px var(--darker), inset 0 -3px var(--darker);
  }
  body code {
    color: var(--light);
    border-color: var(--darker);
    background-color: var(--dark);
  }
  body pre {
    border-radius: var(--radius);
    color: var(--light);
    border-color: var(--darker);
    background-color: var(--dark);
  }
  body pre.bg-light {
    background-color: var(--darken) !important;
  }
  body pre,
  body code,
  body kbd,
  body samp {
    font-family: var(--mono-font);
  }
  body input[type="checkbox"],
  body input[type="radio"],
  body input[type="checkbox"]:focus,
  body input[type="radio"]:focus,
  body input[type="checkbox"]:hover:active,
  body input[type="radio"]:hover:active,
  body input[type="checkbox"]:disabled,
  body input[type="radio"]:disabled,
  body input[type="checkbox"]:hover:active:disabled,
  body input[type="radio"]:hover:active:disabled {
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    appearance: none !important;
    height: 1em !important;
    width: 1em !important;
    border: 1px solid !important;
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--darker) !important;
  }
  body input[type="checkbox"]:hover,
  body input[type="radio"]:hover {
    box-shadow: 0 0 2px 1px var(--lighter) !important;
  }
  body input[type="radio"] {
    border-radius: 100% !important;
  }
  body input[type="radio"]:checked {
    border-color: var(--shadow) !important;
    background-color: var(--accent) !important;
  }
  body input[type="checkbox"]:checked {
    border-color: var(--shadow) !important;
    background-color: var(--accent) !important;
  }
  body input[type="checkbox"]:indeterminate {
    border-color: var(--shadow) !important;
    background-color: var(--red) !important;
  }

  input[type="radio"]:checked {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><circle cx="256" cy="256" r="128" fill="white"/></svg>') !important;
    background-size: cover !important;
  }
  input[type="checkbox"]:checked {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><path fill="white" d="M99 213l-1 89 104 102 205-201v-91L203 315 99 213z"/></svg>') !important;
    background-size: cover !important;
  }
  input[type="checkbox"]:indeterminate {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><rect x="128" y="192" width="256" height="96" fill="white"/></svg>') !important;
    background-size: cover !important;
  }
  table:not(.code):not(.shortcut-mappings) {
    display: table !important;
    border-collapse: unset;
    border-spacing: 0px;
    border-radius: var(--radius);
    background-color: var(--dark) !important;
  }
  table:not(.code):not(.shortcut-mappings) thead {
    background-color: var(--t) !important;
  }
  table:not(.code):not(.shortcut-mappings) thead > th,
  table:not(.code):not(.shortcut-mappings) thead tr:first-child > th {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--darken) !important;
    border-top: 1px solid var(--darker) !important;
    border-bottom: 1px solid var(--darker) !important;
  }
  table:not(.code):not(.shortcut-mappings) thead > th:not(:last-child),
  table:not(.code):not(.shortcut-mappings) thead tr:first-child > th:not(:last-child) {
    border-right: none !important;
  }
  table:not(.code):not(.shortcut-mappings) thead > th:first-child,
  table:not(.code):not(.shortcut-mappings) thead tr:first-child > th:first-child {
    border-top-left-radius: var(--radius);
    border-left: 1px solid var(--darker);
  }
  table:not(.code):not(.shortcut-mappings) thead > th:last-child,
  table:not(.code):not(.shortcut-mappings) thead tr:first-child > th:last-child {
    border-top-right-radius: var(--radius);
    border-right: 1px solid var(--darker);
  }
  table:not(.code):not(.shortcut-mappings) tbody {
    background-color: var(--t) !important;
  }
  table:not(.code):not(.shortcut-mappings) tbody td {
    word-break: break-all;
    border: none !important;
    border-left: 1px solid var(--darker) !important;
    border-bottom: 1px solid var(--darker) !important;
