}*/
/* width */
::-webkit-scrollbar {
	width: 6px;
	height: 6px;
}

/* Track */
::-webkit-scrollbar-track {
	background: rgba(0, 0, 0, 0);
}

/* Handle */
::-webkit-scrollbar-thumb {
	background: var(--atom-one-dark-mono-3);
	border-radius: 10px;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
	background: var(--atom-one-dark-mono-3);
}

/* Handle */
::-webkit-scrollbar-thumb:active {
	background: var(--atom-one-dark-blue);
}

/* Controls colour of self */
.cm-s-jupyter span.cm-variable-2 {
	color: var(--atom-one-dark-orange-1);
}

/* Controls colour of self */
.cm-s-jupyter span.cm-variable-3 {
	color: var(--atom-one-dark-green);
}


/* Controls colour of built in like print, range, etc */
.cm-s-jupyter span.cm-builtin {
	color: var(--atom-one-dark-cyan);
}


/* Center any plots e.g. Matplotlib plots */
.p-Widget.jp-RenderedImage.jp-mod-trusted.jp-OutputArea-output {
	/*display: table-cell;*/
	text-align: center;
	/*vertical-align: middle;*/
}

.jp-Completer-item.jp-mod-active {

	color: var(--atom-one-dark-fg);
	background: var(--atom-one-dark-bg-hl);
}


/* Custom line rule */
.jp-RenderedHTMLCommon hr {
	display: block;
	margin-top: 0.5em;
	margin-bottom: 0.5em;
	margin-left: 0em;
	margin-right: 0em;
	border-style: inset;
	border-width: 1px;
	border-top: 1px solid var(--atom-one-dark-fg);
	border-bottom: 1px solid var(--atom-one-dark-fg);
}

/* Markdown inline code */
.jp-RenderedHTMLCommon code {
	background-color: var(--atom-one-dark-gray);
	padding: 1px 3px;
}

.jp-MarkdownOutput {
	flex: 1 1 auto;
	margin-top: 0;
	margin-bottom: 0;
	padding-left: var(--jp-code-padding);
	padding-right: var(--jp-code-padding);
}

.cm-s-zenburn {
	background-color: var(--atom-one-dark-bg);
	color: var(--atom-one-dark-fg);
}

.cm-s-zenburn .CodeMirror-gutters {
	background: var(--atom-one-dark-bg) !important;
}

.ace_editor {
    background-color: #232323;
    color: #93A1A1;
    font-size: 12px;
    font-family: 'Inconsolata','Menlo';
}

.ace_identifier {
    color: #c76363;
}

.ace_gutter {
    background: #01313f;
    color: #d0edf7;
}

.ace_print-margin {
    width: 1px;
    background: #33555E;
}

.ace_entity.ace_other.ace_attribute-name, .ace_storage {
    color: #93A1A1;
}

.ace_cursor, .ace_string.ace_regexp {
    color: #D30102;
}

.ace_marker-layer .ace_active-line, .ace_marker-layer .ace_selection {
    background: #232323;
}

.ace_multiselect .ace_selection.ace_start {
    box-shadow: 0 0 3px 0px #002B36;
    border-radius: 2px;
}

.ace_marker-layer .ace_step {
    background: rgb(102, 82, 0);
}

.ace_marker-layer .ace_bracket {
    margin: -1px 0 0 -1px;
    border: 1px solid rgba(147, 161, 161, 0.50);
}

.ace_gutter-active-line {
    background-color: #0d3440;
}

.ace_marker-layer .ace_selected-word {
    border: 1px solid #073642;
}

.ace_invisible {
    color: rgba(147, 161, 161, 0.50);
}

.ace_keyword, .ace_meta, .ace_support.ace_class, .ace_support.ace_type {
    color: #859900;
}

.ace_constant.ace_character, .ace_constant.ace_other {
    color: #CB4B16;
}

.ace_constant.ace_language {
    color: #B58900;
}

.ace_constant.ace_numeric {
    color: #D33682;
}

.ace_fold {
    background-color: #268BD2;
    border-color: #93A1A1;
}

.ace_entity.ace_name.ace_function, .ace_entity.ace_name.ace_tag, .ace_support.ace_function, .ace_variable, .ace_variable.ace_language {
    color: #268BD2;
}

.ace_string {
    color: #2AA198;
}

.ace_comment {
    font-style: italic;
    color: #657B83;
}

.ace_indent-guide {
    opacity: 0.15;
}

.ace_line.ends_with_foreign:not(.starts_with_foreign) .ace_token, .ace_line.ends_with_foreign:not(.starts_with_foreign) .ace_indent-guide {
    background-color: transparent;
}

.ace_gutter-cell.modified {
    background-color: #8f9489;
}

.ace_paren {
    color: #93a1a1;
}
